library(phytools)
library(TreeSim)
library(geiger)

source("unittest_R_utils.R")

## BD
## params
b.rate <- 1.0
d.rate <- 0.8

## simulation specs
n.batches <- 100
n.sim <- 100
size.tr <- 10 # for simulations with sim.bd.taxa
max.age <- 3.0 # for simulations with sim.bd.age

## storing things
trs.list.max.taxa <- rep(list(vector(mode="list", length=n.sim)), n.batches) # store trees with different ages, same number of extant taxa
spec.df.max.taxa <- as.data.frame(matrix(rep(0, n.batches * n.sim * 3), nrow=n.batches * n.sim))

trs.list.max.t <- rep(list(vector(mode="list", length=n.sim)), n.batches) # store trees with different numbers of extant taxa, same origin age (but different root age)
spec.df.max.t <- as.data.frame(matrix(rep(0, n.batches * n.sim * 3), nrow=n.batches * n.sim))

names(spec.df.max.taxa) <- names(spec.df.max.t) <-  c("ntotal", "ntips", "root.age")


######## max.taxa ########
set.seed(123)
null.count <- 0
for (b in 1:n.batches) {
    null.count = 0 # in case we want to know anything about this later

    if (b %% 5 == 0) { cat("batch ", b, "\r") }

    ## no rejection sampling required
    ## trs.max.taxa = sim.bd.taxa(size.tr, n.sim, b.rate, d.rate, complete=TRUE, frac=1)
    trs.max.taxa = list(vector(mode="list", length=n.sim))
    for (j in 1:n.sim) {
        trs.max.taxa[[j]] = sim.bdtree(b=b.rate, d=d.rate, n=size.tr, stop="taxa", extinct=FALSE) # geiger

        ## phytools always returns a tree, extinct or notm no matter what
        ## a.tr = pbtree(b=b.rate, d=d.rate, n=size.tr)
        ## conditioning on survival
        ## while (length(getExtant(a.tr)) < size.tr) {
        ##     a.tr = pbtree(b=b.rate, d=d.rate, n=size.tr)
        ## }
        ## trs.max.taxa[[j]] = a.tr
    }

    for (i in 1:n.sim) {
        tr.max.taxa = trs.max.taxa[[i]]

        ## conditioning on speciation and survival
        ## (TreeSim only returns "valid" trees, which means having size.tr extant tips)
        trs.list.max.taxa[[b]][[i]] = tr.max.taxa # b-th batch, i-th tree with max.taxa
        ith.specs.df.max.taxa = get.specs(tr.max.taxa, w.states=FALSE, w.root=TRUE)
        spec.df.max.taxa$root.age[(b-1) * n.batches + i] = ith.specs.df.max.taxa$"tr.h"
        spec.df.max.taxa$ntotal[(b-1) * n.batches + i] = ith.specs.df.max.taxa$"n.total"
        spec.df.max.taxa$ntips[(b-1) * n.batches + i] = ith.specs.df.max.taxa$"n.tips"
    }
}

## getting stats (mean, stdev, stderr) for different tree specs
mean.df.max.taxa <- data.frame(matrix(rep(0.0, 3 * n.batches), nrow=n.batches))
sd.df.max.taxa <- data.frame(matrix(rep(0.0, 3 * n.batches), nrow=n.batches))
stderr.df.max.taxa <- data.frame(matrix(rep(0.0, 3 * n.batches), nrow=n.batches))
names(mean.df.max.taxa) <- names(sd.df.max.taxa) <- names(stderr.df.max.taxa) <- c("n.total", "n.tips", "root.age")
root.age.ci.width.max.taxa <- n.total.ci.width.max.taxa <- rep(0.0, n.batches)
for (b in 1:n.batches) {
    bth.batch.df.all = spec.df.max.taxa[((b-1) * n.batches + 1):((b-1) * n.batches + n.sim),]
    bth.batch.df = bth.batch.df.all[bth.batch.df.all$ntips==size.tr,] # for max taxa
    ## bth.batch.df = bth.batch.df.all ## for max age
    mean.df.max.taxa[b,] = sapply(bth.batch.df, mean)
    sd.df.max.taxa[b,] = sapply(bth.batch.df, sd)
    stderr.df.max.taxa[b,] = sd.df.max.taxa[b,] / sqrt(n.sim)

    root.age.ci.width.max.taxa[b] = 1.96 * stderr.df.max.taxa[b, "root.age"]
    n.total.ci.width.max.taxa[b] = 1.96 * stderr.df.max.taxa[b, "n.total"]
}

## getting means and 95-CI widths (for unit test)
cat("\nn_total_mean_maxtaxa = [", paste(mean.df.max.taxa$n.total, collapse=", "), "]\n")
cat("\nn_total_ci_width_maxtaxa = [", paste(n.total.ci.width.max.taxa, collapse=", "), "]\n")
cat("\nroot_ages_mean_maxtaxa = [", paste(mean.df.max.taxa$root.age, collapse=", "), "]\n")
cat("\nroot_ages_ci_width_maxtaxa = [", paste(root.age.ci.width.max.taxa, collapse=", "), "]\n")

