######## max.t ########
set.seed(123)
null.count <- 0
for (b in 1:n.batches) {
    null.count = 0 # in case we want to know anything about this later

    if (b %% 5 == 0) { cat("batch ", b, "\r") }

    ## conditioning on survival with TreeSim
    trs.max.t = list(vector(mode="list", length=n.sim))
    j = 1
    while (j <= 100) {
        ## extinct=False conditions on survival for geiger
        tr.max.t = sim.bdtree(b=b.rate, d=d.rate, t=max.age, stop="time", extinct=FALSE)
        trs.max.t[[j]] = tr.max.t
        j = j+1

        ## phytools always returns a tree, extinct or notm no matter what
        ## tr.max.t = pbtree(b=b.rate, d=d.rate, t=max.age)

        ## ## conditioning on survival!
        ## if ((max.age - max(nodeHeights(tr.max.t)[,2])) <= 1e-8) {
        ##     trs.max.t[[j]] = tr.max.t
        ##     j = j+1
        ## }

        ## TreeSim
        ## tr.max.t = sim.bd.age(max.age, 1, b.rate, d.rate, frac=1, complete=TRUE, mrca=TRUE)
        ## conditioning on survival with TreeSim
        ## if (class(tr.max.t) == "list" && class(tr.max.t[[1]][1]) == "list") {
        ##     trs.max.t[[j]] = tr.max.t[[1]]
        ##     j = j + 1
        ## }
    }

    for (i in 1:n.sim) {
        tr.max.t = trs.max.t[[i]]
        trs.list.max.t[[b]][[i]] = tr.max.t # b-th batch, i-th tree with max.t
        ith.specs.df.max.t = get.specs(tr.max.t, w.states=FALSE, w.sa=FALSE, w.root=TRUE)
        spec.df.max.t$root.age[(b-1) * n.batches + i] = ith.specs.df.max.t$"tr.h"
        spec.df.max.t$ntotal[(b-1) * n.batches + i] = ith.specs.df.max.t$"n.total"
        spec.df.max.t$ntips[(b-1) * n.batches + i] = ith.specs.df.max.t$"n.tips"
    }
}

## getting stats (mean, stdev, stderr) for different tree specs
mean.df.max.t <- data.frame(matrix(rep(0.0, 3 * n.batches), nrow=n.batches))
sd.df.max.t <- data.frame(matrix(rep(0.0, 3 * n.batches), nrow=n.batches))
stderr.df.max.t <- data.frame(matrix(rep(0.0, 3 * n.batches), nrow=n.batches))
names(mean.df.max.t) <- names(sd.df.max.t) <- names(stderr.df.max.t) <- c("n.total", "n.tips", "root.age")
n.tips.ci.width.max.t <- n.total.ci.width.max.t <- rep(0.0, n.batches)
for (b in 1:n.batches) {
    bth.batch.df.all = spec.df.max.t[((b-1) * n.batches + 1):((b-1) * n.batches + n.sim),]
    bth.batch.df = bth.batch.df.all ## for max t
    mean.df.max.t[b,] = sapply(bth.batch.df, mean)
    sd.df.max.t[b,] = sapply(bth.batch.df, sd)
    stderr.df.max.t[b,] = sd.df.max.t[b,] / sqrt(n.sim)

    # root.age.ci.width.max.t[b] = 1.96 * stderr.df.max.t[b, "root.age"]
    n.tips.ci.width.max.t[b] = 1.96 * stderr.df.max.t[b, "n.tips"]
    n.total.ci.width.max.t[b] = 1.96 * stderr.df.max.t[b, "n.total"]
}

## getting means and 95-CI widths (for unit test)
cat("\nn_total_mean_maxt = [", paste(mean.df.max.t$n.total, collapse=", "), "]\n")
cat("\nn_total_ci_width_maxt = [", paste(n.total.ci.width.max.t, collapse=", "), "]\n")
cat("\nn_extant_mean_maxt = [", paste(mean.df.max.t$n.tips, collapse=", "), "]\n")
